import React, { useState } from "react";


const SignUp=()=>{
    const [avatar, setAvatar]=useState('hers is images')
    const [lfile, setLfile]=useState(null)

    console.log(avatar);

   const onFileUpload = () => {
    
        // Create an object of formData
        const formData = new FormData();
      
        // Update the formData object
        formData.append(
          "myFile",
         lfile,
          lfile.name
        );
      
        // Details of the uploaded file
        console.log(lfile);
      
        // Request made to the backend api
        // Send formData object
        // axios.post("api/uploadfile", formData);
      };

    return(
        <div>
            <h1>hello this is SignUp page</h1>
            <form>
                <div className="email">
                    {/* <Label>Email</Label> */}
                    <input type="Email" placeholder="inter your email"/>
                </div>
                <div className="password">
                    {/* <lable>Password</lable> */}
                    <input type="password" placeholder="inter your password"/>
                </div>
                <div className="repassword">
                    {/* <lable>Password</lable> */}
                    <input type="password" placeholder="repeat your password"/>
                </div>
                <div className="avatar">
                    {/* <lable>avatar</lable> */}
                    <input type="file" placeholder="avatar" onChange={e=>setAvatar( e.target.files[0]) }/>
                </div>
                <div className="submit">
                    {/* <lable>avatar</lable> */}
                    <input type="submit" title="Submit"onClick={this.onFileUpload}/>
                </div>
                
            </form>
            <div>
                <img src={avatar&&avatar.default} alt={avatar}/>
            </div>

        </div>
    )
}
export default SignUp
