import React, { useState ,useEffect} from "react";
import { Api } from "../api/Api";



const SignUp=()=>{
    // const [Username, setUsername]=useState('')
    const [email, setEmail]=useState('')
    const [password1, setPassword1]=useState('')
    const [password2, setPassword2]=useState('')
    const [token, setToken]=useState('')

    const efect = token.key?window.location.href="/account":'please inter the right information'
useEffect(()=>efect,[token])

   const signupClicked = () => {

    Api.RegesterUser({email,password1,password2})
    .then(res=>setToken(res)) 
    .catch(error=>console.log(error))

    // const lfile = new FormData();
    // lfile.append("image",avatar,avatar.name)
  
    // axios.post("api/endpoint/lfile")
   }
    

    return(
        <div>
            <h1>hello this is SignUp page</h1>
           
                <div className="email">
                    <input type="email"  onChange={e=>setEmail(e.target.value)} value={email} placeholder="Email"/>
                </div>
                <div className="password1">
                    <input type="password"  onChange={e=>setPassword1(e.target.value)} value={password1} placeholder="repeat your password1"/>
                </div>
                <div className="password2">
                    <input type="password"  onChange={e=>setPassword2(e.target.value)} value={password2} placeholder="repeat your password2"/>
                </div>
                
                <div className="submit">
                    <input type="submit" title="Submit"onClick={signupClicked}/>
                </div>
                {/* <p>{efect}</p> */}

        </div>
    )
}
export default SignUp;
