import React, { useEffect, useState } from "react";
import { Api } from "../api/Api";


const SignIn=()=>{

    const [email,setEmail]=useState('')
    const [password,setPassword]=useState('')
    const [username,setUsername]=useState('')
    const [token,setToken]=useState('')


    const mesage= token?window.location.href="/":'your emeil or password is wrong try again'

    useEffect(()=>mesage,[token])

    const signinClicked= ()=>{
         Api.SignInUser({username,email,password})
        .then(res=> setToken(res.key))
        .then(error =>console.log(error))
        

    }

    return(
        <div>
     
            <h1>hello this is Signin page</h1>
           
                 <div className="username">
                    <input type="text"  onChange={e=>setUsername(e.target.value)} value={username} placeholder="inter your username"/>
                </div>

                <div className="email">
                    <input type="email"  onChange={e=>setEmail(e.target.value)} value={email} placeholder="inter your email"/>
                </div>
                <div className="password1">
                    <input type="password"  onChange={e=>setPassword(e.target.value)} value={password} placeholder="repeat your password1"/>
                </div>           
                <div className="submit">
                    <input type="submit" title="Submit"onClick={signinClicked}/>
                </div>
                
           
                
            
        </div>
    )
}
export default SignIn
